const articlesByLang = []

// Iterate all URLs and inject postImage values into the right language objects
for (article of $('Get article by ID').first().json.article.languages) {
  if(article.hreflang === "es" || article.hreflang === "sp"){
    articlesByLang.push(article)
  }
}

// Return the modified article for your HTTP node
return articlesByLang

