if($input.first().json.body){
  const data = $input.first().json.body
    // Remove bracket-style links: [text] or [//url]
    .replace(/\[[^\]]*\]/g, '. ')
    // Remove multiple spaces/newlines
    .replace(/\s{2,}/g, ' ')
    // Remove breadcrumbs like "1. Health 2. Sexual Health ..."
    .replace(/\d+\.\s.*?(?=[A-Z]{2,}|Published|Jump to|$)/, ' ')
    // Remove "We earn a commission..." disclaimers
    .replace(/We earn a commission[\s\S]*?(?=[A-Z]{2,})/, ' ')
    .trim();

  return {data};
}

