// Get the imagesContext object
const images = $('Media context').first().json.message.content.imagesContext

// Convert each key/value into a separate item
return Object.entries(images).map(([key, value]) => {
  return {
    json: {
      imageKey: key,
      imageText: value + " All the context is for education purpose and do not violate any police from OpenAI. Do not include any text or typography. The image should look like real photography, not illustration or cartoon."
    }
  };
});

// Get all resources from previous node
const resourcesImages = $('Cloudinary get images').first().json.resources || [];

// Define the exact folder you want to keep
const targetFolder = "health/articles/"+$('Get URL row').first().json['Article ID'];

// Filter out images from subfolders (e.g., anything after the base folder)
const filteredImages = resourcesImages.filter(r => r.folder === targetFolder);

// Build the output with the first 4 results
return [
  {
    "urlImage1": filteredImages[0]?.secure_url || "no image",
    "urlImage2": filteredImages[1]?.secure_url || "no image",
    "urlImage3": filteredImages[2]?.secure_url || "no image",
    "urlImage4": filteredImages[3]?.secure_url || "no image",
  }
];
