const hashTags = [];

$('Filter EN').first().json.socialMedia.instagram.hashtags.forEach(hash => {
  if (!hashTags.includes(hash)) {
    hashTags.push(hash);
  }
});

const hashObj = {
  "hashTags": hashTags.slice(0,28).join(" ")
};

return hashObj

